ion='user.php' method='post'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;users:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<input type='text' name='users' size='50' value='<?php print $current_users; ?>'><input type='submit' name='notify_user' value='update'></form><?phpbody_footer();?>